sp_widget.client_script:
function($interval){

	var c = this;

	var el = $interval(function() {

		var progressBar = $("div#tiered div.animated-progress span");

		if ( progressBar ) {
			$interval.cancel(el);
			$(".animated-progress span").each(function() {
				$(this).animate(
					{
						width: $(this).attr("data-progress") + "%"
					},
					5000
				);
				$(this).text($(this).attr("data-progress") + "%");
			});
		}

	}, 100);

}

